import React, { useContext } from 'react';
import styled, { ThemeContext, useTheme } from 'styled-components';
import { Page } from '@/store/features/toolbar/toolbarSlice';

interface IconProps {
  active: boolean;
  color?: string;
  backgroundColor?: string;
  dropShadow?: string;
  icon?: Page;
  onClick?: (e: React.MouseEvent<HTMLElement>) => void;
  render?: (node: React.ReactNode) => React.ReactNode;
  children?: React.ReactNode;
  className?: string;
  width?: string;
  height?: string;
}

const StyledIcon = styled.div<IconProps>`
  padding: ${({ width }) => (width ? 'calc()' : '0.7rem')};
  padding-right: 1.2rem;
  background-color: ${({ active, theme }) =>
    active ? theme.colors.base.darkBG : theme.colors.base.darkerBG};
  border-radius: 0.5rem;
  position: relative;
  cursor: pointer;

  div {
    width: ${({ width }) => width};
    height: ${({ height, width }) => height || width};
    border-radius: 0.5rem;
    display: grid;
    place-items: center;
    transition: 0.3s;
    background-color: ${({ active, theme }) =>
      active ? theme.colors.primary : theme.colors.base.darkerBG};
    color: ${({ color, active, theme }) =>
      active ? theme.colors.white : color || theme.colors.primary};
    box-shadow: ${({ active, theme }) =>
      active ? '0 8px 24px ' + theme.colors.base.orange : 'none'};
    z-index: ${({ active }) => (active ? 2 : 1)};
  }

  &.active div::before {
    content: '';
    position: absolute;
    top: -1rem;
    right: 0;
    width: 1rem;
    height: 1rem;
    border-radius: 50%;
    background-color: ${({ theme }) => theme.colors.base.darkerBG};
    z-index: 4;
    box-shadow: 0.5rem 0.5rem 0 ${({ theme }) => theme.colors.base.darkBG};
  }

  &.active div::after {
    content: '';
    position: absolute;
    bottom: -1rem;
    right: 0;
    width: 1rem;
    height: 1rem;
    border-radius: 50%;
    background-color: ${({ theme }) => theme.colors.base.darkerBG};
    z-index: 4;
    box-shadow: 0.5rem -0.5rem 0 ${({ theme }) => theme.colors.base.darkBG};
  }

  & svg {
    transition: 0.3s;
    transform: scale(1.2);
  }
  & div:hover svg {
    transform: scale(1.4);
  }
`;

export const Icon: React.FC<IconProps> = (props: IconProps) => {
  const theme = useContext(ThemeContext);
  const color = props.active ? theme.colors.white : theme.colors.primary;

  switch (props.icon) {
    case 'home':
      return <HomeIcon {...props} color={color} />;
    case 'discount':
      return <DiscountIcon {...props} color={color} />;
    case 'pie':
      return <PieIcon {...props} color={color} />;
    case 'bell':
      return <BellIcon {...props} color={color} />;
    case 'message':
      return <MessageIcon {...props} color={color} />;
    case 'settings':
      return <SettingsIcon {...props} color={color} />;
    case 'delete':
      return <DeleteIcon {...props} color={color} />;
    default:
      return null;
  }
};

